  name: "Navbar",
  data: () => ({
    links: ["Робота з нами", "Проблеми", "Переваги", "Команда"],
    dialog: false,
  }),
  methods: {
    dialogMethod() {
      if (this.dialog === false) {
        this.dialog = true;
      } else {
        this.dialog = false;
      }
    },
  },
  computed: {
    dialog: {
      get () {
        return this.value
      },
      set (value) {
        this.$emit('setModal', value)
      }
    }
  }
};
